package Przeksztalcenia;

public class Polaczenia {
	private Punkt a,b;
	
	public Polaczenia(Punkt A, Punkt B) {
		// TODO Auto-generated constructor stub
		a=A;
		b=B;
	}

	public Punkt getA() {
		return a;
	}

	public void setA(Punkt a) {
		this.a = a;
	}

	public Punkt getB() {
		return b;
	}

	public void setB(Punkt b) {
		this.b = b;
	}
}
